async function fetchMeals() {
  const fetchedMeals = await fetch(
    "https://react-http-97bae-default-rtdb.firebaseio.com/Meals.json"
  );
  const meals = await fetchedMeals.json();
  const mealsArray = [];
  for (const key in meals) {
    mealsArray.push({
      id: key,
      name: meals[key].name,
      description: meals[key].description,
      price: meals[key].price,
    });
  }
  return mealsArray;
}

function AvailableMeals() {
  const meals = fetchMeals();
  const mealsList = meals.map((meal) => <MealItem meal={meal} key={meal.id} />);
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
}

export default AvailableMeals;

