import React, { useContext, useState } from "react";
import CartContext from "../../store/cart-context";
import Modal from "../UI/Modal";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";
import Checkout from "./CheckOut";

function Cart(props) {
  const ctxCart = useContext(CartContext);
  const hasItems = ctxCart.items.length > 0;
  const cartItemAddHandler = (item) => {
    ctxCart.addItem({ ...item, amount: 1 });
  };
  const cartItemRemoveHandler = (id) => {
    ctxCart.removeItem(id);
  };
  const cartList = ctxCart.items.map((item) => (
    <CartItem
      key={item.id}
      name={item.name}
      price={item.price}
      amount={item.amount}
      onAdd={cartItemAddHandler.bind(null, item)}
      onRemove={cartItemRemoveHandler.bind(null, item.id)}
    />
  ));
  // const modalActions = () => { }
  const orderButtonHandler = () => {
    setIsCheckout(true);
  };
  const [isCheckout, setIsCheckout] = useState(false);

  return (
    <Modal onbackdropClick={props.oncloseCart}>
      <ul className={classes["cart-items"]}>{cartList}</ul>
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>Rs.{ctxCart.totalAmount}</span>
      </div>
      {isCheckout && <Checkout />}
      {/* {!isCheckout && (
        
      )} */}
    </Modal>
  );
}

export default Cart;
