import { useRef, useState } from "react";
import classes from "./CheckOut.module.css";

const Checkout = (props) => {
  const nameInputRef = useRef();
  const streetInputRef = useRef();
  const postalCodeInputRef = useRef();
  const cityInputRef = useRef();

  const isEmpty = (value) => value.trim() === "";
  const notHave5Digits = (value) => value.trim().length !== 5;

  const [refState, setRefState] = useState({
    nameInputRef: true,
    streetInputRef: true,
    postalCodeInputRef: true,
    cityInputRef: true,
  });

  const confirmHandler = (event) => {
    event.preventDefault();
    if (isEmpty(nameInputRef.current)) setRefState({ nameInputRef: false });
    if (isEmpty(streetInputRef.current)) setRefState({ streetInputRef: false });
    if (isEmpty(cityInputRef.current)) setRefState({ cityInputRef: false });
    if (notHave5Digits(postalCodeInputRef.current))
      setRefState({ postalCodeInputRef: false });
  };

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input ref={nameInputRef} type="text" id="name" />
      </div>
      <div className={classes.control}>
        <label htmlFor="street">Street</label>
        <input ref={streetInputRef} type="text" id="street" />
      </div>
      <div className={classes.control}>
        <label htmlFor="postal">Postal Code</label>
        <input ref={postalCodeInputRef} type="text" id="postal" />
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input ref={cityInputRef} type="text" id="city" />
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
