import { useRef, useState } from "react";
import classes from "./CheckOut.module.css";

const Checkout = (props) => {
  const nameInputRef = useRef();
  const streetInputRef = useRef();
  const postalCodeInputRef = useRef();
  const cityInputRef = useRef();

  const isEmpty = (value) => value.trim() === "";
  const notHave5Digits = (value) => value.trim().length !== 5;

  const [refState, setRefState] = useState({
    nameInputRef: true,
    streetInputRef: true,
    postalCodeInputRef: true,
    cityInputRef: true,
  });

  const confirmHandler = (event) => {
    event.preventDefault();
    console.log(nameInputRef.current.value);
    console.log(streetInputRef.current.value);
    console.log(cityInputRef.current.value);
    console.log(postalCodeInputRef.current.value);
    if (isEmpty(nameInputRef.current.value))
      setRefState({ nameInputRef: false });
    if (isEmpty(streetInputRef.current.value))
      setRefState({ streetInputRef: false });
    if (isEmpty(cityInputRef.current.value))
      setRefState({ cityInputRef: false });
    if (notHave5Digits(postalCodeInputRef.current.value))
      setRefState({ postalCodeInputRef: false });
    if (
      (refState.nameInputRef &&
        refState.streetInputRef &&
        refState.postalCodeInputRef &&
        refState.cityInputRef) === true
    ) {
      console.log("Submit Form");
      return;
    }
    console.log(refState);
  };

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input ref={nameInputRef} type="text" id="name" />
        {refState.nameInputRef === false ? (
          <p>Please enter a valid name.</p>
        ) : (
          ""
        )}
      </div>
      <div className={classes.control}>
        <label htmlFor="street">Street</label>
        <input ref={streetInputRef} type="text" id="street" />
        {refState.streetInputRef === false ? (
          <p>Please enter a valid street.</p>
        ) : (
          ""
        )}
      </div>
      <div className={classes.control}>
        <label htmlFor="postal">Postal Code</label>
        <input ref={postalCodeInputRef} type="text" id="postal" />
        {refState.postalCodeInputRef === false ? (
          <p>Please enter a valid postal code (5 characters long).</p>
        ) : (
          ""
        )}
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input ref={cityInputRef} type="text" id="city" />
        {refState.cityInputRef === false ? (
          <p>Please enter a valid city.</p>
        ) : (
          ""
        )}
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
