//   {
//     id: "m3",
//     name: "Chicken Handi",
//     description: "Pakistan special handi",
//     price: 450,
//   },
//   {
//     id: "m4",
//     name: "Malai Boti",
//     description: "Healthy and tendered Chicken",
//     price: 300,
//   },
// ];

function AvailableMeals() {
  const [mealList, setMealList] = useState([]);

  async function fetchMeals() {
    const fetchedMeals = await fetch(
      "https://react-http-97bae-default-rtdb.firebaseio.com/Meals.json"
    );
    const meals = await fetchedMeals.json();
    const mealsArray = [];
    for (const key in meals) {
      mealsArray.push({
        id: key,
        name: meals[key].name,
        description: meals[key].description,
        price: meals[key].price,
      });
    }
    setMealList(mealsArray);
  }
  useEffect(() => {
    fetchMeals();
  }, [fetchMeals]);

  const mealsList = mealList.map((meal) => (
    <MealItem meal={meal} key={meal.id} />
  ));
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
}

export default AvailableMeals;

